import React, { useContext, useState, useEffect, useRef } from "react";
import { Container as InteractiveImageContainer } from "../shared/interactive-image";
import { Link, useHistory } from "react-router-dom";
import { StateContext, ActionTypes } from "../util/useApplicationState";
import styled from "styled-components";
import { useStopwatch } from "react-timer-hook";

const Container = styled(InteractiveImageContainer)`
  background-image: url("/office/bombdefusalUI.jpg");
`;

const TimerWrapper = styled.div`
  position: absolute;
  left: 40%;
  right: 40%;
  top: 55%;
  bottom: auto;

  display: grid;
  grid-template: 1fr / repeat(3, 1fr);
  grid-gap: 20%;

  & > p {
    font-size: 50px;
    color: red;
    border: none;
    text-transform: uppercase;
    background-color: white;
  }
`;

const InputWrapper = styled.div`
  position: absolute;
  left: 18.5%;
  right: 18.5%;
  top: 80%;
  bottom: 14.5%;

  display: grid;
  grid-template: 1fr / repeat(4, 1fr);
  grid-gap: 3.5%;

  & > input {
    color: #000;
    border: none;
    text-transform: uppercase;
  }
`;

const unlockCode = ["stay", "hom", "fur", "evr"];

export default function BombRoom() {
  const [dataInput, _setDataInput] = useState(["", "", "", ""]);
  const timeoutRef = useRef();

  const setData = (index, value) => {
    _setDataInput((prevVal) => {
      const newArr = [...prevVal];
      newArr[index] = value;
      return newArr;
    });
  };

  useEffect(() => {
    const isValid = dataInput.every(
      (value, index) => unlockCode[index].toLowerCase() === value.toLowerCase()
    );

    if (isValid) {
      timeoutRef.current = setTimeout(() => {
        alert("Puzzle unlocked");
      }, 250);
    }
  }, [dataInput]);

  useEffect(
    () => () => timeoutRef.current && clearTimeout(timeoutRef.current),
    []
  );

  // STOPWATCH
  // const {
  //   seconds,
  //   minutes,
  //   hours,
  //   days,
  //   isRunning,
  //   start,
  //   pause,
  //   reset,
  // } = useStopwatch({ autoStart: true });
  // STOPWATCH END

  const time = new Date();
  time.setSeconds(time.getSeconds() + 600); // 10 minutes timer
  const {
    seconds,
    minutes,
    hours,
    days,
    isRunning,
    start,
    pause,
    resume,
    restart,
  } = useTimer({
    expiryTimestamp,
    onExpire: () => console.warn("onExpire called"),
  });

  const formattedTime =
    seconds < 10
      ? `${hours}:${minutes}:0${seconds}`
      : `${hours}:${minutes}:${seconds}`;

  return (
    <Container>
      <TimerWrapper>
        <p> {hours} </p>
        <p> {minutes} </p>
        <p> {seconds} </p>
      </TimerWrapper>

      <InputWrapper>
        <input
          type="text"
          value={dataInput[0]}
          onChange={(e) => setData(0, e.target.value)}
        />
        <input
          type="text"
          value={dataInput[1]}
          onChange={(e) => setData(1, e.target.value)}
        />
        <input
          type="text"
          value={dataInput[2]}
          onChange={(e) => setData(2, e.target.value)}
        />
        <input
          type="text"
          value={dataInput[3]}
          onChange={(e) => setData(3, e.target.value)}
        />
      </InputWrapper>
    </Container>
  );
}
