import React, { useContext, useState, useEffect } from "react";
import InteractiveImage from "../interactive-image";
import { Link, useHistory } from "react-router-dom";
import { StateContext, ActionTypes } from "../../hooks/useApplicationState";
import styled from "styled-components";

const InputContainer = styled.div`
  display: flex;
  input {
    margin-right: 24px;
  }

  p {
    margin-right: 12px;
  }
`;

export default function PetWall() {
  const history = useHistory();
  const { state, dispatch } = useContext(StateContext);
  const [codeInput, setCodeInput] = useState("");
  const [petwallSource, setPetWallSource] = useState("./office/pet_wall_original.png")
  useEffect(() => {
    if (codeInput == "6510") {
      setPetWallSource(() => { })
      setTimeout(() => {
        dispatch({ type: "SET_STATE", payload: "LOCKSCREEN" });
        history.push("/outside_office");
      }, 150);
    }
  }, [codeInput]);

  return (
    <InteractiveImage src={petwallSource}>
      <InputContainer>
        <p>Dog Code:</p>
        <input
          value={codeInput}
          onChange={(e) => {
            setCodeInput(e.target.value);
          }}
        />
      </InputContainer>
    </InteractiveImage>
  );
}
