import React, { useContext, useState, useEffect } from "react";
import InteractiveImage from "../interactive-image";
import { Link, useHistory } from "react-router-dom";
import { StateContext, ActionTypes } from "../../hooks/useApplicationState";
import styled from "styled-components";

const InputContainer = styled.div`
  display: flex;
  input {
    margin-right: 24px;
  }

  p {
    margin-right: 12px;
  }
`;

export default function PetWall() {
  const history = useHistory();
  const { state, dispatch } = useContext(StateContext);
  const [costInput, setCostInput] = useState("");
  const [dataInput, setDataInput] = useState("");

  useEffect(() => {
    if (costInput == "6" && dataInput == "55") {
      setTimeout(() => {
        dispatch({ type: "SET_STATE", payload: "LOCKSCREEN" });
        history.push("/outside_office");
      }, 150);
    }
  }, [costInput, dataInput]);

  return (

      <InputContainer>
        <p>$$:</p>
        <input
          value={costInput}
          onChange={(e) => {
            setCostInput(e.target.value);
          }}
        />
        <p>GB:</p>
        <input
          value={dataInput}
          onChange={(e) => {
            setDataInput(e.target.value);
          }}
        />
      </InputContainer>
  );
}
