import React, { useContext, useState, useEffect } from "react";
import InteractiveImage from "../interactive-image";
import { Link, useHistory } from "react-router-dom";
import { StateContext, ActionTypes } from "../../hooks/useApplicationState";
import styled from "styled-components";

const InputContainer = styled.div`
  display: flex;
  input {
    margin-right: 24px;
  }

  p {
    margin-right: 12px;
  }
`;

const Modal = styled.div`
display: ${(props) => {props.display? "block" :  "none"}}
`

export default function FantasyWall() {
  const history = useHistory();
  const { state, dispatch } = useContext(StateContext);
  const [codeInput, setCodeInput] = useState("");
  const [input, setInput] = useState("");
  const [colorState, setColorState] = useState("white");
  useEffect(() => {
    if (codeInput == "") {
      setTimeout(() => {
        //dispatch({ type: "SET_STATE", payload: "LOCKSCREEN" });
        //  history.push("/pet_wall_answer");
      }, 150);
    }
    if (codeInput > "0") {
      setTimeout(() => {
        setColorState("red");
      }, 500);
    }
  }, [codeInput]);

  return (
      <>
          <Modal display={false}> This i smy Modal</Modal>
      <InteractiveImage animationPlay={true} src="./office/FantasyMap.jpg">
        <InputContainer>
          <p>Grid Code:</p>
          <input
            value={input}
            onChange={(e) => {
              setInput(e.target.value);
              setColorState("white");
            }}
            style={{ backgroundColor: colorState }}
          />
          <button
            onClick={() => {
              setCodeInput(input);
            }}
          >
            {" "}
            Enter Code{" "}
          </button>
        </InputContainer>
      </InteractiveImage>
    </>
  );
}
